import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io.connect("http://localhost:5000");

function Chat({ username }) {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [file, setFile] = useState(null);

  const sendMessage = () => {
    if (message.trim() !== "" || file) {
      const data = { user: username, text: message, attachment: null };

      if (file) {
        const reader = new FileReader();
        reader.onloadend = () => {
          data.attachment = { name: file.name, type: file.type, data: reader.result };
          socket.emit('send_message', data);
          setMessage("");
          setFile(null);
        };
        reader.readAsDataURL(file);
      } else {
        socket.emit('send_message', data);
        setMessage("");
      }
    }
  };

  useEffect(() => {
    socket.emit('new_user', username);

    socket.on('receive_message', (data) => {
      setChatHistory((prev) => [...prev, data]);
    });

    return () => socket.off('receive_message');
  }, [username]);

  return (
    <div className="chatContainer">
      <h3>Welcome, {username}</h3>
      <div className="messages">
        {chatHistory.map((msg, idx) => (
          <div key={idx} className={`message ${msg.user === username ? "self" : ""}`}>
            {msg.system ? (
              <em className="system">{msg.text}</em>
            ) : (
              <>
                <strong>{msg.user}: </strong>
                {msg.text}
                {msg.attachment && (
                  <div className="attachment">
                    {msg.attachment.type.startsWith("image/") ? (
                      <img src={msg.attachment.data} alt={msg.attachment.name} />
                    ) : (
                      <a href={msg.attachment.data} download={msg.attachment.name}>
                        ðŸ“Ž {msg.attachment.name}
                      </a>
                    )}
                  </div>
                )}
              </>
            )}
          </div>
        ))}
      </div>
      <div className="inputContainer">
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          style={{ margin: "0 5px" }}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
