import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';
import { FiPlus } from 'react-icons/fi'; // icon from react-icons

const socket = io.connect("http://localhost:5000");

function Chat({ username }) {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const fileInputRef = useRef(null);

  const sendMessage = () => {
    if (message.trim() !== "" || fileInputRef.current.files.length > 0) {
      const data = { user: username, text: message, attachment: null };

      const file = fileInputRef.current.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onloadend = () => {
          data.attachment = { name: file.name, type: file.type, data: reader.result };
          socket.emit('send_message', data);
          setMessage("");
          fileInputRef.current.value = "";
        };
        reader.readAsDataURL(file);
      } else {
        socket.emit('send_message', data);
        setMessage("");
      }
    }
  };

  useEffect(() => {
    socket.emit('new_user', username);
    socket.on('receive_message', (data) => {
      setChatHistory((prev) => [...prev, data]);
    });
    return () => socket.off('receive_message');
  }, [username]);

  return (
    <div className="chatContainer">
      <h3>Welcome, {username}</h3>
      <div className="messages">
        {chatHistory.map((msg, idx) => (
          <div key={idx} className={`message ${msg.user === username ? "self" : ""}`}>
            {msg.system ? (
              <em className="system">{msg.text}</em>
            ) : (
              <>
                <strong>{msg.user}: </strong>
                {msg.text}
                {msg.attachment && (
                  <div className="attachment">
                    {msg.attachment.type.startsWith("image/") ? (
                      <img src={msg.attachment.data} alt={msg.attachment.name} />
                    ) : (
                      <a href={msg.attachment.data} download={msg.attachment.name}>
                        ðŸ“Ž {msg.attachment.name}
                      </a>
                    )}
                  </div>
                )}
              </>
            )}
          </div>
        ))}
      </div>

      <div className="inputContainer">
        <div className="inputRow">
          <input
            type="text"
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage}>Send</button>
        </div>

        <div className="plusContainer">
          <input
            type="file"
            ref={fileInputRef}
            style={{ display: "none" }}
            onChange={() => sendMessage()}
          />
          <button className="plusBtn" onClick={() => fileInputRef.current.click()}>
            <FiPlus size={24} />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
